import React from 'react';


class CustomerSchedule {
    constructor (customerName, bicycleModel, timeScheduled) {
        this.name = customerName;
        this.model = bicycleModel;
        this.time = timeScheduled;
    }
}

class ScheduleTable extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            schedule : [new CustomerSchedule ("David Thompson", "ATX", new Date (2021, 10, 15)),
                        new CustomerSchedule ("Antonio Valencia", "BPX", new Date (2021, 7, 28)),
                        new CustomerSchedule ("Micheal Kean", "G5030", new Date (2021, 12, 30)),
                        ]
        };
    }

    render () {
        return (
            <table>
                <tr>
                    <th>Customer Name</th>
                    <th>Bicycle Model</th>
                    <th>Date</th>
                </tr>
                <tr>
                {this.state.schedule.map ( (custSchedule, index) => {
                    <React.Fragment>
                        <td>CCC</td>
                        <td>AAA</td>
                        <td>BBB</td>
                    </React.Fragment>
                })}
                </tr>

            </table>

        );
    }
}


class RepairSchedule extends React.Component {
    constructor (props) {
        super (props);
        this.table = new ScheduleTable ();
        this.state = {
            scheduleRender : this.table.render () 
        }
    }



    render () {
        return (
            <React.Fragment>
                <h1>This is repair page</h1>
                {this.state.scheduleRender}
            </React.Fragment>
        );
    }
}


export default RepairSchedule
