import React from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Modal } from '@material-ui/core';





class ScheduleTable extends React.Component {
    
    constructor (props) {
        super (props);
        this.columns = [
            {field: 'id', headerName : 'ID', width : 100},
            {field: 'customerName', headerName : 'Customer Name', width : 300},
            {field: 'bicycleModel', headerName : 'Bicycle Model', width: 100},
            {field: 'date', headerName : 'Date', width: 200}
        ];

        this.rows = [
            {id: 1, customerName : 'Davinson Sanchez', bicycleModel: 'ATX68', date: new Date (2021, 1, 20)},
            {id: 2, customerName : 'Eric Dier', bicycleModel: 'A6800', date: new Date (2021, 3, 5)},
            {id: 3, customerName : 'Thomas Davis', bicycleModel: 'BX9968', date: new Date (2021, 4, 8)},
        ];
        this.state = {
            num : 8
        }
        
    }
    changeNum () {
        this.setState ({num : 10});
        alert ("here")
    }

    render () {
        return (<h1>{this.state.num}</h1>);
        /*
        return (
            <div style={{ height: 400, width: '100%' }}>
                <DataGrid rows={this.rows} columns={this.columns} />
            </div>

        );
        */
    }
}

class ModalForm extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            open : 0,
            randomNum : 7
        }
    }

    openForm () {
        this.setState ({open : 1});
        this.setState ({randomNum : 9});
        
    }
    closeForm () {
        this.setState ({open : true});
    }
    render () {
        alert (this.state.open)
        return (
            <div>
            <h1>{this.state.randomNum}</h1>
        <Dialog open={this.state.open} onClose={this.closeForm()} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here. We will send updates
            occasionally.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={this.closeForm()} color="primary">
            Cancel
          </Button>
          <Button onClick={this.closeForm()} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
      </div>
        );
    }

}




class RepairSchedule extends React.Component {
    constructor (props) {
        super (props);
        this.table = new ScheduleTable ();

        this.state = {
            scheduleRender : this.table.render (),
        }
    }
    openForm () {
        this.table.changeNum();

    }


    render () {
        return (
            <React.Fragment>
            {this.table.render()}
            <Button variant = "contained" color = "primary" onClick = {() => this.openForm()} >Schedule Repair</Button>
            </React.Fragment>
            );
        /*
        return (
            <React.Fragment>
                <h1>This is repair page</h1>
                {this.state.scheduleRender}
                {this.state.modalRender}
                <Button variant = "contained" color = "primary" onClick = {() => this.openForm()} >Schedule Repair</Button>
            </React.Fragment>
        );
        */
    }
}


export default RepairSchedule
