import React from 'react';
import { DataGrid } from '@material-ui/data-grid';


class ScheduleTable extends React.Component {
    
    constructor (props) {
        super (props);
        this.columns = [
            {field: 'id', headerName : 'ID'},
            {field: 'customerName', headerName : 'Customer Name'},
            {field: 'bicycleModel', headerName : 'Bicycle Model'},
            {field: 'date', headerName : 'Date'}
        ];

        this.rows = [
            {customerName : 'Davinson Sanchez', bicycleModel: 'ATX68', date: new Date (2021, 1, 20)},
            {customerName : 'Eric Dier', bicycleModel: 'A6800', date: new Date (2021, 3, 5)},
            {customerName : 'Thomas Davis', bicycleModel: 'BX9968', date: new Date (2021, 4, 8)},
        ];
        
    }

    render () {
        return (
            <div style={{ height: 400, width: '100%' }}>
                <DataGrid rows={this.rows} columns={this.columns} />
            </div>

        );
    }
}


class RepairSchedule extends React.Component {
    constructor (props) {
        super (props);
        this.table = new ScheduleTable ();
        this.state = {
            scheduleRender : this.table.render () 
        }
    }



    render () {
        return (
            <React.Fragment>
                <h1>This is repair page</h1>
                {this.state.scheduleRender}
            </React.Fragment>
        );
    }
}


export default RepairSchedule
