import React from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Modal } from '@material-ui/core';





class ScheduleTable extends React.Component {
    
    constructor (props) {
        super (props);
        this.state = {
            columns : [
                {field: 'id', headerName : 'ID', width : 100},
                {field: 'customerName', headerName : 'Customer Name', width : 300},
                {field: 'bicycleModel', headerName : 'Bicycle Model', width: 100},
                {field: 'date', headerName : 'Date', width: 200}
            ],

            rows : [
                {id: 1, customerName : 'Davinson Sanchez', bicycleModel: 'ATX68', date: new Date (2021, 1, 20)},
                {id: 2, customerName : 'Eric Dier', bicycleModel: 'A6800', date: new Date (2021, 3, 5)},
                {id: 3, customerName : 'Thomas Davis', bicycleModel: 'BX9968', date: new Date (2021, 4, 8)},
            ] 
        }
       
    }

    add_data () {
        this.setState ({
            rows: {id: 1, customerName : 'Davinson Sanchez', bicycleModel: 'ATX68', date: new Date (2021, 1, 20)}
        })
    }


    render () {
        return (
            <div style={{ height: 400, width: '100%' }}>
                <DataGrid rows={this.state.rows} columns={this.state.columns} />
            </div>

        );
        
    }
}

class ModalForm extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            open : false,
        }
    }

    openForm () {
        this.setState ({open : true});
    }
    closeForm () {
        this.setState ({open : false});
    }
    render () {
        return (
            <div>
        <Dialog open={this.state.open} onClose={this.closeForm()} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here. We will send updates
            occasionally.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={this.closeForm()} color="primary">
            Cancel
          </Button>
          <Button onClick={this.closeForm()} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
      </div>
        );
    }

}




class RepairSchedule extends React.Component {
    constructor (props) {
        super (props);
        //this.table = React.createRef ();
        this.modal = React.createRef ();
    }

    aaaaopenForm () {
        //this.modal.current.openForm ();
    }

    render () {
        return (
            <React.Fragment>
                
                <ModalForm ref = {this.modal}/>
                <Button variant = "contained" color = "primary"  >Schedule Repair</Button>
            </React.Fragment>
            );
    }
}


export default RepairSchedule
