import React, {useState, useEffect} from 'react';
import Home from './Home'
import RepairSchedule from './RepairSchedule'
import Header from './Header'

function Router () {

    const [display_page, f] = useState (<Home/>)
    const [state, func] = useState (display_page);
    
    useEffect (() => {
        console.log (state);
        if (state == "a") {
            display_page = <Home/>
        } else {
            display_page = <Header/>
        }
    }, [state]);

    return (
        
        <div>
            <button onClick = {() => {func ("a")}}> A </button>
            <button onClick = {() => {func ("b")}}> B </button>
            {display_page}
        </div>
    );
    
}


export default Router
