import React, {useState, useEffect} from 'react';
import Home from './Home'
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import RepairSchedule from './RepairSchedule'


class Router extends React.Component {
    constructor (props) {
        super (props);
        this.listPage = [new Home (), new RepairSchedule ()];
        this.state = {
            displayPage : this.listPage[0].render()
        };
    }

    switchPage (pageName) {

        if (pageName == "Repair") {
            this.setState ({displayPage : this.listPage[1].render()});
        } else {
            this.setState ({displayPage : this.listPage[0].render()});
        }
    }
  
    render () {
        return (
            <div>
                <AppBar position = "static">
                    <Toolbar>
                        <Button color = "secondary" onClick={() => this.switch_page ("Home")}>Home</Button>
                        <Button color = "secondary" onClick={() => this.switch_page ("Repair")}>Schedule Repair</Button>
                        <Button color = "secondary" onClick={() => this.switch_page ("Inventory")}>Current Invetory</Button>
                    </Toolbar>
                </AppBar>
                {this.state.display_page}
            </div>
        );
    }
    

    
}


export default Router
